import React, { useState, useEffect } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  const handleMenuToggle = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const handleSidebarClose = () => {
    setSidebarOpen(false);
  };

  // Detectar scroll para efeitos visuais
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-navy-50 via-white to-primary-50 relative overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 bg-legal-pattern opacity-30 pointer-events-none"></div>

      {/* Gradient Overlay */}
      <div className="absolute inset-0 bg-gradient-to-br from-transparent via-white/50 to-primary-50/30 pointer-events-none"></div>

      {/* Header com efeito glassmorphism */}
      <div className={`fixed top-0 left-0 right-0 z-40 transition-all duration-300 ${
        isScrolled 
          ? 'bg-white/80 backdrop-blur-lg shadow-soft border-b border-white/20' 
          : 'bg-transparent'
      }`}>
        <Header onMenuToggle={handleMenuToggle} />
      </div>

      <div className="flex pt-16 relative z-10">
        {/* Sidebar com animações elegantes */}
        <div className={`fixed inset-y-0 left-0 z-30 transition-all duration-300 ease-in-out ${
          sidebarOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'
        }`}>
          <Sidebar isOpen={sidebarOpen} onClose={handleSidebarClose} />
        </div>

        {/* Overlay para mobile */}
        {sidebarOpen && (
          <div 
            className="fixed inset-0 bg-navy-900/50 backdrop-blur-sm z-20 lg:hidden animate-fade-in"
            onClick={handleSidebarClose}
          />
        )}

        {/* Main Content com animações */}
        <main className="flex-1 lg:ml-64 transition-all duration-300 ease-in-out">
          <div className="container-main py-8 animate-fadeInUp">
            {/* Content Wrapper com glassmorphism */}
            <div className="bg-white/60 backdrop-blur-sm rounded-3xl shadow-soft border border-white/30 p-8 min-h-[calc(100vh-8rem)] relative overflow-hidden">
              {/* Decorative Elements */}
              <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-br from-primary-200/30 to-gold-200/30 rounded-full blur-3xl"></div>
              <div className="absolute bottom-0 left-0 w-24 h-24 bg-gradient-to-tr from-navy-200/30 to-primary-200/30 rounded-full blur-2xl"></div>

              {/* Content */}
              <div className="relative z-10">
                {children}
              </div>
            </div>
          </div>
        </main>
      </div>

      {/* Floating Action Button (opcional) */}
      <div className="fixed bottom-8 right-8 z-50">
        <button className="btn btn-primary rounded-full w-14 h-14 shadow-strong hover:shadow-glow animate-float">
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 10l7-7m0 0l7 7m-7-7v18" />
          </svg>
        </button>
      </div>

      {/* Loading Indicator (quando necessário) */}
      <div className="fixed top-0 left-0 right-0 h-1 bg-gradient-to-r from-primary-500 to-gold-500 transform scale-x-0 transition-transform duration-300 z-50" id="loading-bar"></div>
    </div>
  );
};

export default Layout;
