import React, { useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { 
  UserIcon, 
  TrophyIcon, 
  CurrencyDollarIcon,
  FireIcon,
  Bars3Icon,
  BellIcon,
  ChevronDownIcon,
  CogIcon,
  ArrowRightOnRectangleIcon,
  UserCircleIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';

interface HeaderProps {
  onMenuToggle: () => void;
}

const Header: React.FC<HeaderProps> = ({ onMenuToggle }) => {
  const { user, logout } = useAuth();
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [notificationsOpen, setNotificationsOpen] = useState(false);

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Erro ao fazer logout:', error);
    }
  };

  const notifications = [
    { id: 1, title: 'Nova conquista desbloqueada!', time: '2 min', type: 'achievement' },
    { id: 2, title: 'Desafio diário disponível', time: '1 hora', type: 'challenge' },
    { id: 3, title: 'Você subiu no ranking!', time: '3 horas', type: 'ranking' },
  ];

  return (
    <header className="relative">
      <div className="px-6 py-4 flex items-center justify-between">
        {/* Left Section */}
        <div className="flex items-center space-x-6">
          {/* Menu Toggle (Mobile) */}
          <button
            onClick={onMenuToggle}
            className="lg:hidden p-2 rounded-xl hover:bg-white/20 transition-all duration-200 hover:scale-105"
          >
            <Bars3Icon className="h-6 w-6 text-navy-700" />
          </button>

          {/* Logo Elegante */}
          <div className="flex items-center space-x-3">
            <div className="relative">
              <div className="bg-gradient-to-br from-primary-500 via-primary-600 to-navy-700 p-3 rounded-2xl shadow-glow animate-float">
                <TrophyIcon className="h-7 w-7 text-white" />
              </div>
              <div className="absolute -top-1 -right-1 w-4 h-4 bg-gold-500 rounded-full animate-pulse"></div>
            </div>
            <div className="hidden sm:block">
              <h1 className="text-2xl font-bold bg-gradient-to-r from-navy-800 to-primary-700 bg-clip-text text-transparent">
                Duolingo Jurídico
              </h1>
              <p className="text-sm text-navy-500 font-medium">
                Sua jornada para a aprovação ⚖️
              </p>
            </div>
          </div>
        </div>

        {/* Right Section */}
        {user && (
          <div className="flex items-center space-x-4">
            {/* Gamification Stats - Modernizado */}
            <div className="hidden md:flex items-center space-x-3 px-5 py-3 bg-white/60 backdrop-blur-sm rounded-2xl border border-white/30 shadow-soft">
              {/* XP com animação */}
              <div className="flex items-center space-x-2 px-3 py-1 bg-gradient-to-r from-gold-100 to-gold-200 rounded-xl">
                <TrophyIcon className="h-5 w-5 text-gold-600" />
                <span className="text-sm font-bold text-gold-800">
                  {user.profile?.xp_points || 0}
                </span>
                <span className="text-xs text-gold-600 font-medium">XP</span>
              </div>

              {/* Coins com brilho */}
              <div className="flex items-center space-x-2 px-3 py-1 bg-gradient-to-r from-warning-100 to-warning-200 rounded-xl">
                <CurrencyDollarIcon className="h-5 w-5 text-warning-600" />
                <span className="text-sm font-bold text-warning-800">
                  {user.profile?.coins || 0}
                </span>
              </div>

              {/* Streak com fogo */}
              <div className="flex items-center space-x-2 px-3 py-1 bg-gradient-to-r from-danger-100 to-danger-200 rounded-xl">
                <FireIcon className="h-5 w-5 text-danger-600" />
                <span className="text-sm font-bold text-danger-800">
                  {user.profile?.current_streak || 0}
                </span>
              </div>
            </div>

            {/* Notifications com dropdown elegante */}
            <div className="relative">
              <button 
                onClick={() => setNotificationsOpen(!notificationsOpen)}
                className="p-3 rounded-xl bg-white/60 backdrop-blur-sm border border-white/30 hover:bg-white/80 transition-all duration-200 hover:scale-105 shadow-soft relative"
              >
                <BellIcon className="h-5 w-5 text-navy-700" />
                {notifications.length > 0 && (
                  <span className="absolute -top-1 -right-1 bg-gradient-to-r from-danger-500 to-danger-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center font-bold animate-pulse">
                    {notifications.length}
                  </span>
                )}
              </button>

              {/* Dropdown de Notificações */}
              {notificationsOpen && (
                <div className="absolute right-0 top-full mt-2 w-80 bg-white/90 backdrop-blur-lg rounded-2xl shadow-strong border border-white/30 z-50 animate-slide-down">
                  <div className="p-4 border-b border-navy-100">
                    <h3 className="font-bold text-navy-800 flex items-center">
                      <SparklesIcon className="h-5 w-5 mr-2 text-primary-600" />
                      Notificações
                    </h3>
                  </div>
                  <div className="max-h-64 overflow-y-auto">
                    {notifications.map((notification) => (
                      <div key={notification.id} className="p-4 hover:bg-primary-50/50 transition-colors border-b border-navy-50 last:border-b-0">
                        <p className="text-sm font-medium text-navy-800">{notification.title}</p>
                        <p className="text-xs text-navy-500 mt-1">{notification.time} atrás</p>
                      </div>
                    ))}
                  </div>
                  <div className="p-3 border-t border-navy-100">
                    <button className="w-full text-center text-sm text-primary-600 font-medium hover:text-primary-700">
                      Ver todas as notificações
                    </button>
                  </div>
                </div>
              )}
            </div>

            {/* User Menu Elegante */}
            <div className="relative">
              <button 
                onClick={() => setUserMenuOpen(!userMenuOpen)}
                className="flex items-center space-x-3 p-3 rounded-2xl bg-white/60 backdrop-blur-sm border border-white/30 hover:bg-white/80 transition-all duration-200 hover:scale-105 shadow-soft"
              >
                <div className="relative">
                  <div className="w-10 h-10 bg-gradient-to-br from-primary-500 via-primary-600 to-navy-700 rounded-xl flex items-center justify-center shadow-medium">
                    {user.profile?.avatar ? (
                      <img 
                        src={user.profile.avatar} 
                        alt={user.first_name} 
                        className="w-10 h-10 rounded-xl object-cover"
                      />
                    ) : (
                      <UserIcon className="h-6 w-6 text-white" />
                    )}
                  </div>
                  <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-success-500 border-2 border-white rounded-full"></div>
                </div>
                <div className="hidden sm:block text-left">
                  <p className="text-sm font-bold text-navy-800">
                    {user.first_name} {user.last_name}
                  </p>
                  <p className="text-xs text-navy-500 font-medium">
                    {user.profile?.experience_level || 'Iniciante'}
                  </p>
                </div>
                <ChevronDownIcon className={`h-4 w-4 text-navy-600 transition-transform duration-200 ${userMenuOpen ? 'rotate-180' : ''}`} />
              </button>

              {/* Dropdown Menu Elegante */}
              {userMenuOpen && (
                <div className="absolute right-0 top-full mt-2 w-64 bg-white/90 backdrop-blur-lg rounded-2xl shadow-strong border border-white/30 z-50 animate-slide-down">
                  <div className="p-4 border-b border-navy-100">
                    <div className="flex items-center space-x-3">
                      <div className="w-12 h-12 bg-gradient-to-br from-primary-500 to-navy-700 rounded-xl flex items-center justify-center">
                        <UserIcon className="h-6 w-6 text-white" />
                      </div>
                      <div>
                        <p className="font-bold text-navy-800">{user.first_name} {user.last_name}</p>
                        <p className="text-sm text-navy-500">{user.email}</p>
                      </div>
                    </div>
                  </div>
                  <div className="py-2">
                    <a
                      href="/profile"
                      className="flex items-center space-x-3 px-4 py-3 text-navy-700 hover:bg-primary-50/50 transition-colors"
                    >
                      <UserCircleIcon className="h-5 w-5" />
                      <span className="font-medium">Meu Perfil</span>
                    </a>
                    <a
                      href="/settings"
                      className="flex items-center space-x-3 px-4 py-3 text-navy-700 hover:bg-primary-50/50 transition-colors"
                    >
                      <CogIcon className="h-5 w-5" />
                      <span className="font-medium">Configurações</span>
                    </a>
                    <a
                      href="/achievements"
                      className="flex items-center space-x-3 px-4 py-3 text-navy-700 hover:bg-primary-50/50 transition-colors"
                    >
                      <TrophyIcon className="h-5 w-5" />
                      <span className="font-medium">Conquistas</span>
                    </a>
                  </div>
                  <div className="border-t border-navy-100 p-2">
                    <button
                      onClick={handleLogout}
                      className="flex items-center space-x-3 w-full px-4 py-3 text-danger-600 hover:bg-danger-50/50 transition-colors rounded-xl"
                    >
                      <ArrowRightOnRectangleIcon className="h-5 w-5" />
                      <span className="font-medium">Sair</span>
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Click outside to close dropdowns */}
      {(userMenuOpen || notificationsOpen) && (
        <div 
          className="fixed inset-0 z-40" 
          onClick={() => {
            setUserMenuOpen(false);
            setNotificationsOpen(false);
          }}
        />
      )}
    </header>
  );
};

export default Header;
