import React from 'react';
import { useLocation } from 'react-router-dom';
import {
  HomeIcon,
  BookOpenIcon,
  QuestionMarkCircleIcon,
  TrophyIcon,
  ChartBarIcon,
  CogIcon,
  ShoppingBagIcon,
  UserGroupIcon,
  SparklesIcon,
  AcademicCapIcon,
  XMarkIcon,
  FireIcon,
  LightBulbIcon,
  StarIcon
} from '@heroicons/react/24/outline';

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

interface NavigationItem {
  name: string;
  href: string;
  icon: React.ComponentType<any>;
  badge?: string;
  description?: string;
  color?: string;
}

const navigation: NavigationItem[] = [
  {
    name: 'Dashboard',
    href: '/',
    icon: HomeIcon,
    description: 'Visão geral do seu progresso',
    color: 'from-primary-500 to-primary-600'
  },
  {
    name: 'Disciplinas',
    href: '/subjects',
    icon: BookOpenIcon,
    description: 'Explore as matérias jurídicas',
    color: 'from-navy-500 to-navy-600'
  },
  {
    name: 'Questões',
    href: '/questions',
    icon: QuestionMarkCircleIcon,
    description: 'Pratique com questões de concursos',
    color: 'from-success-500 to-success-600'
  },
  {
    name: 'Simulados',
    href: '/quizzes',
    icon: AcademicCapIcon,
    description: 'Teste seus conhecimentos',
    color: 'from-warning-500 to-warning-600'
  },
  {
    name: 'Conquistas',
    href: '/achievements',
    icon: TrophyIcon,
    badge: '3',
    description: 'Suas medalhas e troféus',
    color: 'from-gold-500 to-gold-600'
  },
  {
    name: 'Ranking',
    href: '/leaderboard',
    icon: UserGroupIcon,
    description: 'Compare seu desempenho',
    color: 'from-danger-500 to-danger-600'
  },
  {
    name: 'Estatísticas',
    href: '/analytics',
    icon: ChartBarIcon,
    description: 'Acompanhe seu progresso',
    color: 'from-primary-500 to-navy-600'
  },
  {
    name: 'Loja',
    href: '/store',
    icon: ShoppingBagIcon,
    description: 'Compre melhorias e personalizações',
    color: 'from-gold-500 to-warning-600'
  },
  {
    name: 'IA Assistente',
    href: '/ai-assistant',
    icon: SparklesIcon,
    badge: 'NOVO',
    description: 'Sua tutora de estudos personalizada',
    color: 'from-primary-500 to-navy-700'
  }
];

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const location = useLocation();

  const isActivePath = (href: string) => {
    return location.pathname === href || 
           (href !== '/' && location.pathname.startsWith(href));
  };

  return (
    <div className="w-64 h-full bg-white/80 backdrop-blur-lg border-r border-white/30 shadow-soft flex flex-col relative overflow-hidden">
      {/* Background Decorations */}
      <div className="absolute top-0 right-0 w-32 h-32 bg-gradient-to-br from-primary-200/20 to-gold-200/20 rounded-full blur-3xl"></div>
      <div className="absolute bottom-0 left-0 w-24 h-24 bg-gradient-to-tr from-navy-200/20 to-primary-200/20 rounded-full blur-2xl"></div>

      {/* Mobile Close Button */}
      <div className="lg:hidden flex justify-end p-4 relative z-10">
        <button
          onClick={onClose}
          className="p-2 rounded-xl hover:bg-white/20 transition-all duration-200 hover:scale-105"
        >
          <XMarkIcon className="h-6 w-6 text-navy-700" />
        </button>
      </div>

      {/* Logo Section (Mobile) */}
      <div className="lg:hidden px-4 pb-4 relative z-10">
        <div className="flex items-center space-x-3">
          <div className="bg-gradient-to-br from-primary-500 via-primary-600 to-navy-700 p-2 rounded-xl shadow-glow">
            <TrophyIcon className="h-6 w-6 text-white" />
          </div>
          <div>
            <h2 className="text-lg font-bold bg-gradient-to-r from-navy-800 to-primary-700 bg-clip-text text-transparent">
              Duolingo Jurídico
            </h2>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 px-4 pb-4 space-y-2 overflow-y-auto relative z-10">
        {navigation.map((item, index) => {
          const isActive = isActivePath(item.href);

          return (
            <a
              key={item.name}
              href={item.href}
              onClick={onClose}
              className={`
                group flex items-center px-4 py-4 text-sm font-semibold rounded-2xl transition-all duration-300 hover:scale-105 relative overflow-hidden
                ${isActive
                  ? `bg-gradient-to-r ${item.color || 'from-primary-500 to-primary-600'} text-white shadow-glow hover:shadow-strong`
                  : 'text-navy-700 hover:bg-white/60 hover:text-navy-900 hover:shadow-soft'
                }
              `}
              style={{ animationDelay: `${index * 50}ms` }}
            >
              {/* Background Glow Effect */}
              {isActive && (
                <div className={`absolute inset-0 bg-gradient-to-r ${item.color || 'from-primary-500 to-primary-600'} opacity-20 blur-xl`}></div>
              )}

              <div className={`
                mr-4 p-2 rounded-xl flex-shrink-0 transition-all duration-300
                ${isActive 
                  ? 'bg-white/20 shadow-medium' 
                  : 'bg-navy-100/50 group-hover:bg-white/80 group-hover:shadow-soft'
                }
              `}>
                <item.icon
                  className={`
                    h-5 w-5 transition-all duration-300
                    ${isActive ? 'text-white' : 'text-navy-600 group-hover:text-navy-800'}
                  `}
                />
              </div>

              <div className="flex-1 min-w-0 relative z-10">
                <div className="flex items-center justify-between">
                  <span className="truncate font-bold">{item.name}</span>
                  {item.badge && (
                    <span
                      className={`
                        ml-2 px-2 py-1 text-xs font-bold rounded-full transition-all duration-300 animate-pulse
                        ${isActive
                          ? 'bg-white/30 text-white shadow-soft'
                          : item.badge === 'NOVO'
                          ? 'bg-gradient-to-r from-success-500 to-success-600 text-white shadow-glow'
                          : 'bg-gradient-to-r from-primary-500 to-primary-600 text-white shadow-glow'
                        }
                      `}
                    >
                      {item.badge}
                    </span>
                  )}
                </div>
                {item.description && (
                  <p
                    className={`
                      text-xs mt-1 truncate font-medium
                      ${isActive ? 'text-white/80' : 'text-navy-500 group-hover:text-navy-600'}
                    `}
                  >
                    {item.description}
                  </p>
                )}
              </div>

              {/* Active Indicator */}
              {isActive && (
