import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import {
  TrophyIcon,
  FireIcon,
  CurrencyDollarIcon,
  ChartBarIcon,
  BookOpenIcon,
  QuestionMarkCircleIcon,
  ClockIcon,
  SparklesIcon,
  StarIcon,
  CalendarDaysIcon,
  AcademicCapIcon,
  ArrowTrendingUpIcon,
  BoltIcon,
  PlayIcon,
  EyeIcon,
  RocketLaunchIcon,
  LightBulbIcon,
  ShieldCheckIcon,
  GiftIcon,
  BeakerIcon
} from '@heroicons/react/24/outline';
import {
  TrophyIcon as TrophySolid,
  FireIcon as FireSolid,
  StarIcon as StarSolid,
  HeartIcon as HeartSolid
} from '@heroicons/react/24/solid';

interface DashboardStats {
  xp_points: number;
  coins: number;
  current_streak: number;
  best_streak: number;
  total_study_time: number;
  questions_answered: number;
  correct_answers: number;
  subjects_studied: number;
  achievements_earned: number;
}

interface RecentActivity {
  id: number;
  type: 'question' | 'lesson' | 'achievement' | 'quiz';
  title: string;
  description: string;
  timestamp: string;
  xp_earned?: number;
  icon: React.ComponentType<any>;
  color: string;
}

interface DailyGoal {
  type: string;
  current: number;
  target: number;
  icon: React.ComponentType<any>;
  color: string;
}

interface StudyStreak {
  day: string;
  completed: boolean;
  xp: number;
}

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [recentActivities, setRecentActivities] = useState<RecentActivity[]>([]);
  const [dailyGoals, setDailyGoals] = useState<DailyGoal[]>([]);
  const [studyStreak, setStudyStreak] = useState<StudyStreak[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  // Mock data - em produÃ§Ã£o, viria da API
  useEffect(() => {
    const mockStats: DashboardStats = {
      xp_points: user?.profile?.xp_points || 2850,
      coins: user?.profile?.coins || 540,
      current_streak: user?.profile?.current_streak || 12,
      best_streak: user?.profile?.best_streak || 28,
      total_study_time: user?.profile?.total_study_time || 4320, // em minutos
      questions_answered: 287,
      correct_answers: 231,
      subjects_studied: 7,
      achievements_earned: 15
    };

    const mockActivities: RecentActivity[] = [
      {
        id: 1,
        type: 'achievement',
        title: 'Mestre da ConsistÃªncia! ðŸ†',
        description: 'Manteve uma sequÃªncia de 12 dias consecutivos',
        timestamp: '2024-01-15T10:30:00Z',
        xp_earned: 150,
        icon: TrophySolid,
        color: 'from-gold-500 to-gold-600'
      },
      {
        id: 2,
        type: 'quiz',
        title: 'Simulado Direito Constitucional ðŸ“š',
        description: 'Pontuou 92% no simulado de 30 questÃµes',
        timestamp: '2024-01-15T09:15:00Z',
        xp_earned: 120,
        icon: AcademicCapIcon,
        color: 'from-primary-500 to-navy-600'
      },
      {
        id: 3,
        type: 'lesson',
        title: 'Direitos Fundamentais AvanÃ§ado ðŸŽ¯',
        description: 'Completou mÃ³dulo sobre garantias constitucionais',
        timestamp: '2024-01-14T16:45:00Z',
        xp_earned: 85,
        icon: BookOpenIcon,
        color: 'from-success-500 to-success-600'
      },
      {
        id: 4,
        type: 'question',
        title: 'SequÃªncia Perfeita! âš¡',
        description: 'Acertou 15 questÃµes consecutivas',
        timestamp: '2024-01-14T14:20:00Z',
        xp_earned: 75,
        icon: BoltIcon,
        color: 'from-warning-500 to-warning-600'
      }
    ];

    const mockDailyGoals: DailyGoal[] = [
      {
        type: 'QuestÃµes Respondidas',
        current: 18,
        target: 25,
        icon: QuestionMarkCircleIcon,
        color: 'from-primary-500 to-primary-600'
      },
      {
        type: 'Tempo de Estudo',
        current: 75,
        target: 90,
        icon: ClockIcon,
        color: 'from-success-500 to-success-600'
      },
      {
        type: 'XP Conquistado',
        current: 280,
        target: 350,
        icon: TrophyIcon,
        color: 'from-gold-500 to-gold-600'
      },
      {
        type: 'LiÃ§Ãµes ConcluÃ­das',
        current: 3,
        target: 5,
        icon: BookOpenIcon,
        color: 'from-purple-500 to-purple-600'
      }
    ];

    const mockStudyStreak: StudyStreak[] = [
      { day: 'Seg', completed: true, xp: 250 },
      { day: 'Ter', completed: true, xp: 180 },
      { day: 'Qua', completed: true, xp: 320 },
      { day: 'Qui', completed: true, xp: 290 },
      { day: 'Sex', completed: true, xp: 410 },
      { day: 'SÃ¡b', completed: true, xp: 380 },
      { day: 'Dom', completed: false, xp: 0 }
    ];

    setStats(mockStats);
    setRecentActivities(mockActivities);
    setDailyGoals(mockDailyGoals);
    setStudyStreak(mockStudyStreak);
    setLoading(false);
  }, [user]);

  // Atualizar horÃ¡rio em tempo real
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const formatTime = (minutes: number) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const getAccuracyPercentage = () => {
    if (!stats || stats.questions_answered === 0) return 0;
    return Math.round((stats.correct_answers / stats.questions_answered) * 100);
  };

  const getTimeAgo = (timestamp: string) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffInHours = Math.floor((now.getTime() - time.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) return 'Agora hÃ¡ pouco';
    if (diffInHours < 24) return `${diffInHours}h atrÃ¡s`;
    return `${Math.floor(diffInHours / 24)}d atrÃ¡s`;
  };

  const getProgressPercentage = (current: number, target: number) => {
    return Math.min((current / target) * 100, 100);
  };

  const getGreeting = () => {
    const hour = currentTime.getHours();
    if (hour < 12) return 'Bom dia';
    if (hour < 18) return 'Boa tarde';
    return 'Boa noite';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="relative">
          <div className="loading-spinner w-12 h-12"></div>
          <div className="absolute inset-0 bg-gradient-to-r from-primary-500 to-gold-500 rounded-full animate-ping opacity-20"></div>
        </div>
        <p className="ml-6 text-navy-600 font-medium text-lg">Carregando seu universo jurÃ­dico...</p>
      </div>
    );
  }

  return (
    <div className="space-y-8 animate-fadeInUp">
      {/* Hero Section Modernizado */}
      <div className="relative overflow-hidden bg-gradient-to-br from-primary-600 via-primary-700 to-navy-800 rounded-3xl p-8 text-white shadow-strong">
        {/* Background Decorations AvanÃ§adas */}
        <div className="absolute top-0 right-0 w-96 h-96 bg-gradient-to-br from-white/10 to-transparent rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-0 left-0 w-64 h-64 bg-gradient-to-tr from-gold-500/20 to-transparent rounded-full blur-2xl animate-float"></div>
        <div className="absolute top-1/2 left-1/3 w-32 h-32 bg-gradient-to-r from-success-500/10 to-transparent rounded-full blur-xl animate-bounce-slow"></div>

        <div className="relative z-10">
          <div className="flex flex-col lg:flex-row items-start lg:items-center justify-between space-y-6 lg:space-y-0">
            <div className="flex-1">
              <div className="flex items-center space-x-3 mb-4">
                <div className="flex items-center space-x-2">
                  <SparklesIcon className="h-6 w-6 text-gold-400 animate-pulse" />
                  <span className="text-gold-400 font-semibold text-sm">
                    {getGreeting()}, futuro(a) aprovado(a)!
                  </span>
                </div>
                <div className="px-3 py-1 bg-white/20 rounded-full">
                  <span className="text-xs font-bold text-white">
                    {currentTime.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' })}
                  </span>
                </div>
              </div>

              <h1 className="text-4xl lg:text-5xl font-bold mb-3 bg-gradient-to-r from-white via-gold-200 to-white bg-clip-text text-transparent">
                OlÃ¡, {user?.first_name}! ðŸ‘‹
              </h1>

              <p className="text-primary-100 text-lg lg:text-xl font-medium mb-6">
                VocÃª estÃ¡ no caminho certo para a aprovaÃ§Ã£o! ðŸš€
              </p>

              {/* Quick Stats Inline */}
              <div className="flex flex-wrap items-center gap-4">
                <div className="flex items-center space-x-2 px-4 py-2 bg-white/20 rounded-2xl backdrop-blur-sm">
                  <FireSolid className="h-5 w-5 text-orange-400" />
                  <span className="font-bold">{stats?.current_streak} dias</span>
                  <span className="text-sm opacity-80">sequÃªncia</span>
                </div>
                <div className="flex items-center space-x-2 px-4 py-2 bg-white/20 rounded-2xl backdrop-blur-sm">
                  <TrophySolid className="h-5 w-5 text-gold-400" />
                  <span className="font-bold">{stats?.xp_points.toLocaleString()}</span>
                  <span className="text-sm opacity-80">XP</span>
                </div>
                <div className="flex items-center space-x-2 px-4 py-2 bg-white/20 rounded-2xl backdrop-blur-sm">
                  <ChartBarIcon className="h-5 w-5 text-green-400" />
                  <span className="font-bold">{getAccuracyPercentage()}%</span>
                  <span className="text-sm opacity-80">precisÃ£o</span>
                </div>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex flex-col space-y-3">
              <button className="btn btn-gold btn-lg group">
                <PlayIcon className="h-5 w-5 mr-2 group-hover:animate-pulse" />
                Continuar Estudando
                <RocketLaunchIcon className="h-4 w-4 ml-2 group-hover:translate-x-1 transition-transform" />
              </button>
              <button className="btn btn-outline text-white border-white/30 hover:bg-white/10">
                <BeakerIcon className="h-5 w-5 mr-2" />
                LaboratÃ³rio Tech
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid Modernizado */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* XP Card */}
        <div className="card card-hover group">
          <div className="flex items-center justify-between mb-4">
            <div className="p-3 bg-gradient-to-br from-gold-500 to-gold-600 rounded-2xl shadow-glow-gold group-hover:scale-110 transition-transform duration-300">
              <TrophySolid className="h-8 w-8 text-white" />
            </div>
            <div className="text-right">
              <p className="text-3xl font-bold text-navy-800">{stats?.xp_points.toLocaleString()}</p>
              <p className="text-sm text-navy-500 font-medium">Pontos XP</p>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-xs text-navy-400">PrÃ³ximo nÃ­vel</span>
            <span className="text-xs font-bold text-gold-600">+{1000 - (stats?.xp_points || 0) % 1000} XP</span>
          </div>
          <div className="w-full bg-navy-100 rounded-full h-2 mt-2">
            <div 
              className="bg-gradient-to-r from-gold-500 to-gold-600 h-2 rounded-full transition-all duration-500"
              style={{ width: `${((stats?.xp_points || 0) % 1000) / 10}%` }}
            ></div>
          </div>
        </div>

        {/* Streak Card */}
        <div className="card card-hover group">
          <div className="flex items-center justify-between mb-4">
            <div className="p-3 bg-gradient-to-br from-danger-500 to-warning-500 rounded-2xl shadow-glow group-hover:scale-110 transition-transform duration-300">
              <FireSolid className="h-8 w-8 text-white" />
            </div>
            <div className="text-right">
              <p className="text-3xl font-bold text-navy-800">{stats?.current_streak}</p>
              <p className="text-sm text-navy-500 font-medium">Dias Seguidos</p>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-xs text-navy-400">Recorde pessoal</span>
            <span className="text-xs font-bold text-danger-600">{stats?.best_streak} dias</span>
          </div>
          <div className="flex space-x-1 mt-2">
            {studyStreak.map((day, index) => (
              <div
                key={index}
                className={`flex-1 h-2 rounded-full transition-all duration-300 ${
                  day.completed 
                    ? 'bg-gradient-to-r from-danger-500 to-warning-500' 
                    : 'bg-navy-100'
                }`}
              ></div>
            ))}
          </div>
        </div>

        {/* Accuracy Card */}
        <div className="card card-hover group">
          <div className="flex items-center justify-between mb-4">
            <div className="p-3 bg-gradient-to-br from-success-500 to-success-600 rounded-2xl shadow-glow group-hover:scale-110 transition-transform duration-300">
              <ChartBarIcon className="h-8 w-8 text-white" />
            </div>
            <div className="text-right">
              <p className="text-3xl font-bold text-navy-800">{getAccuracyPercentage()}%</p>
              <p className="text-sm text-navy-500 font-medium">PrecisÃ£o</p>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-xs text-navy-400">QuestÃµes corretas</span>
            <span className="text-xs font-bold text-success-600">{stats?.correct_answers}/{stats?.questions_answered}</span>
          </div>
          <div className="w-full bg-navy-100 rounded-full h-2 mt-2">
            <div 
              className="bg-gradient-to-r from-success-500 to-success-600 h-2 rounded-full transition-all duration-500"
              style={{ width: `${getAccuracyPercentage()}%` }}
            ></div>
          </div>
        </div>

        {/* Study Time Card */}
        <div className="card card-hover group">
          <div className="flex items-center justify-between mb-4">
            <div className="p-3 bg-gradient-to-br from-primary-500 to-navy-600 rounded-2xl shadow-glow group-hover:scale-110 transition-transform duration-300">
              <ClockIcon className="h-8 w-8 text-white" />
            </div>
            <div className="text-right">
              <p className="text-3xl font-bold text-navy-800">{Math.floor((stats?.total_study_time || 0) / 60)}</p>
              <p className="text-sm text-navy-500 font-medium">Horas Estudadas</p>
            </div>
          </div>
          <div className="flex items-center justify-between">
            <span className="text-xs text-navy-400">Esta semana</span>
            <span className="text-xs font-bold text-primary-600">{formatTime((stats?.total_study_time || 0) % 420)}</span>
          </div>
          <div className="w-full bg-navy-100 rounded-full h-2 mt-2">
            <div 
              className="bg-gradient-to-r from-primary-500 to-navy-600 h-2 rounded-full transition-all duration-500"
              style={{ width: `${Math.min(((stats?.total_study_time || 0) % 420) / 4.2, 100)}%` }}
            ></div>
          </div>
        </div>
      </div>

      {/* Daily Goals Section */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-gradient-to-br from-primary-500 to-primary-600 rounded-xl">
              <StarSolid className="h-6 w-6 text-white" />
            </div>
            <div>
              <h2 className="text-2xl font-bold text-navy-800">Metas DiÃ¡rias</h2>
              <p className="text-navy-500">Mantenha o foco e alcance seus objetivos</p>
            </div>
          </div>
          <div className="text-right">
            <p className="text-sm text-navy-500">Progresso geral</p>
            <p className="text-2xl font-bold text-primary-600">
              {Math.round(dailyGoals.reduce((acc, goal) => acc + getProgressPercentage(goal.current, goal.target), 0) / dailyGoals.length)}%
            </p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {dailyGoals.map((goal, index) => (
            <div key={index} className="p-4 bg-gradient-to-br from-navy-50 to-primary-50 rounded-2xl border border-navy-100 hover:shadow-medium transition-all duration-300 group">
              <div className="flex items-center justify-between mb-3">
                <div className={`p-2 bg-gradient-to-br ${goal.color} rounded-xl group-hover:scale-110 transition-transform duration-300`}>
                  <goal.icon className="h-5 w-5 text-white" />
                </div>
                <span className="text-xs font-bold text-navy-600">
                  {Math.round(getProgressPercentage(goal.current, goal.target))}%
                </span>
              </div>
              <h3 className="font-semibold text-navy-800 mb-2">{goal.type}</h3>
              <div className="flex items-center justify-between mb-2">
                <span className="text-2xl font-bold text-navy-800">{goal.current}</span>
                <span className="text-sm text-navy-500">/ {goal.target}</span>
              </div>
              <div className="w-full bg-white rounded-full h-2">
                <div 
                  className={`bg-gradient-to-r ${goal.color} h-2 rounded-full transition-all duration-500`}
                  style={{ width: `${getProgressPercentage(goal.current, goal.target)}%` }}
                ></div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Recent Activities & Study Streak */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Activities */}
        <div className="lg:col-span-2 card">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-gradient-to-br from-success-500 to-success-600 rounded-xl">
                <BoltIcon className="h-6 w-6 text-white" />
              </div>
              <div>
                <h2 className="text-2xl font-bold text-navy-800">Atividades Recentes</h2>
                <p className="text-navy-500">Seus Ãºltimos progressos</p>
              </div>
            </div>
            <button className="btn btn-ghost btn-sm">
              <EyeIcon className="h-4 w-4 mr-2" />
              Ver Todas
